const audio=document.getElementById("audio"),playPauseBtn=document.getElementById("playPauseBtn");function updateButton(){playPauseBtn.textContent=audio.paused?"Afspelen":"Pauze"}playPauseBtn.addEventListener("click",()=>{audio.paused?audio.play():audio.pause(),updateButton()}),audio.addEventListener("ended",updateButton),audio.addEventListener("pause",updateButton),audio.addEventListener("play",updateButton),updateButton();