function toggleContent(a){for(var b=document.getElementById(a),c=b.classList.contains("active"),d=document.getElementsByClassName("content"),e=0;e<d.length;e++)d[e].classList.remove("active");for(var f=document.getElementsByClassName("tab"),e=0;e<f.length;e++)f[e].classList.remove("active");c||(b.classList.add("active"),event.target.classList.add("active"))}